class Solution {
public:
    vector<int> getRow(int rowIndex) {
        int n = rowIndex;
        //initialise ans vector for if rowIndex is 0 or 1
        vector<int>ans(n+1,1);
        if(n==0||n==1)
        return ans;

        //make prev <1,1> 
        vector<int>prev(2,1);

        n=rowIndex-1;

        //increase size of ans by 1. start updating ans from previous. Once loop is completed,store ans in           prev 

        while(n--){
            ans.resize(prev.size()+1,1);
            for(int i=1;i<ans.size()-1;i++){
                ans[i]=prev[i-1]+prev[i];
            }    
                prev = ans;

        }
        return ans;
    }
};
