    const hasmap = new Map();
    for (let i = 0; i < nums.length; i++) {
        // Check if the difference betweend duplicates is less than k
        if (i - hasmap.get(nums[i]) <= k) {
            return true;
        }
        hasmap.set(nums[i], i);
    }
    return false;
};
