class Solution {
public:
    bool containsNearbyDuplicate(vector<int>& nums, int k) {

        unordered_map<int, int> mp;
        for(int i=0; i<nums.size(); i++){

		//  If element not found then insert it
            if(mp.find(nums[i])  ==  mp.end())
                mp[nums[i]] = i;

				//  Otherwise if found then check the window size
            else{

			//  If window-size <= k  --->>  return true;
                if((i - mp[nums[i]])  <=  k)
                    return true;
                else

				//  Otherwise update key value
                    mp[nums[i]] = i;
            }
        }
        return false;
    }
};
