            if len(nums)<=k:
                return len(nums) >  len(set(nums))

            hashSet=set(nums[:k])
            if len(hashSet) < k:
                return True

            for i in xrange(k,len(nums)):
                hashSet.add(nums[i])
                if len(hashSet)==k:
                    return True
                else:
                    hashSet.remove(nums[i-k])
            return False

