typedef struct {
    int val;
    int idx;
} Arr_t;

int cmp (const void * a, const void * b) {
    Arr_t *m = (Arr_t *)a;
    Arr_t *n = (Arr_t *)b;
    if (m->val != n->val) {
        return m->val - n->val;
    }
    else {
        return m->idx - n->idx;
    }
}

int absdiff(int a, int b) {
    return a > b ? a - b : b - a;
}

bool containsNearbyDuplicate(int* nums, int numsSize, int k){
    Arr_t *arr = (Arr_t*) malloc(numsSize * sizeof(Arr_t));
    for (int i = 0; i < numsSize; i++) {
        arr[i].val = nums[i];
        arr[i].idx = i;
    }

    qsort(arr, numsSize, sizeof(Arr_t), cmp);

    for (int i = 1; i < numsSize; i++) {
        if (arr[i].val == arr[i-1].val && absdiff(arr[i].idx, arr[i-1].idx) <= k) {
            return true;
        }
    }

    return false;
}
