public class Solution {
    public bool ContainsNearbyDuplicate(int[] nums, int k) {

        Dictionary<int, int> duplicateIndex = new Dictionary<int, int>();

        for(int i = 0; i < nums.Length; i++) {

            //Add value to dictionary when seen for first time; key is the number, value is its index in nums
            if(!duplicateIndex.ContainsKey(nums[i])){
                duplicateIndex.Add(nums[i], i);
            }
            //Already is in dictionary
            else{
                //What is the difference between this index and where it was last seen?
                int indexDifference = i - duplicateIndex[nums[i]];
                //less than k, return true
                if(indexDifference <= k) {
                    return true;
                }
                //If not, update last seen to this current position since last position and next time seen are greater than this position and next time seen by definition
                else {
                    duplicateIndex[nums[i]] = i;
                }                
            }            
        }

        return false;
    }
}
