bool containsNearbyDuplicate(int* nums, int numsSize, int k) {
    int* order = malloc(sizeof(int)*numsSize);
    int flag = 1;
    for (int i = 0; i < numsSize; i++)
    {
        order[i] = i;
    }
    while (flag)
    {
        flag = 0;
        for(int i = 0; i < numsSize-1; i++)
        {
            if (nums[i] > nums[i+1])
            {
                nums[i] ^= nums[i+1];
                nums[i+1] ^= nums[i];
                nums[i] ^= nums[i+1];

                order[i] ^= order[i+1];
                order[i+1] ^= order[i];
                order[i] ^= order[i+1];
                flag = 1;
            }
            else if(nums[i] == nums[i+1] && abs(order[i], order[i+1]) <= k)
            {
                return true;
            }
        }
    }
    return false;
}
