class Solution:
    def containsNearbyDuplicate(self, nums: List[int], k: int) -> bool:
        stat, q = defaultdict(int), deque([])
        for num in nums:
            stat[num] += 1
            q.append(num)
            if stat[num] > 1:
                return True
            if len(q) == k + 1:
                stat[q.popleft()] -= 1
        return False
