const containsNearbyDuplicate = (nums: number[], k: number): boolean => {
  const lastIndexMap = new Map<number, number>();
  for (let i = 0; i < nums.length; i++) {
    if (i - lastIndexMap.get(nums[i]) <= k) {
      return true;
    }
    lastIndexMap.set(nums[i], i);
  }
  return false;
};


// Complexity analysis - Hashmap approach
// Time: O(n)
// Space: O(n)
//
// Submission: 15% Time, 15% memory.
//
const containsNearbyDuplicate = (nums: number[], k: number): boolean => {
    const numsMap = new Map<number, number[]>();

    for (let i = 0; i < nums.length; i++) {
        numsMap.set(nums[i], [...(numsMap.get(nums[i])||[]), i]);
    };

    for (let indexes of numsMap.values()) {
        if (indexes.length < 2) continue;

        for (let x = 1; x < indexes.length; x++) {
            if ((indexes[x] - indexes[x-1]) <= k) return true;
        }
    }

    return false;
}

// Complexity analysis - Two pointer approach.
// Time: O(n^2)
// Space: O(1)
//
// Submission: 10% Time, 90% memory.
//
function containsNearbyDuplicate(nums: number[], k: number): boolean {
    if (nums.length === 1) return false;

    let left = 0, right = 1;

    while(left < right && left <= nums.length) {
        if (right - left > k || right >= nums.length) {
            left++;
            right = left+1;
            continue;
        }

        if (nums[left] === nums[right]) return true;

        right++;
    }

    return false;
};
