class Solution {
    public boolean containsNearbyDuplicate(int[] nums, int k) {
        if (k == 0) return false;

        Map<Integer, Integer> hashMap = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            int j = nums[i];
            if (hashMap.containsKey(j) && i - hashMap.get(j) <= k) return true;
            hashMap.put(j, i);
        }
        return false;
    }
}

