class TrieNode{
	public :
	char data;
	TrieNode* child[26];
	int wordend;
	bool is_end;
	TrieNode(){
		wordend=0;
		is_end=false;
		for(int i=0; i<26; i++){
			child[i]=NULL;
		}
	}
	void insert(TrieNode* root,string s){
		TrieNode* curr=root;
		for(int i=0; i<s.size(); i++){
			int  index=s[i]-'a';
			if(curr->child[index]==NULL){
				curr->child[index]= new TrieNode();
			}
			curr->child[index]->wordend++;
			curr=curr->child[index];
		}
		curr->is_end=true;
	}
	int count_prefix(TrieNode* root,string prefix){
		TrieNode* curr=root;
		for(int i=0; i<prefix.size(); i++){
			int index=prefix[i]-'a';
			if(curr->child[index]==NULL)
				return 0;
			curr=curr->child[index];
		}
		return curr->wordend;        
	}

};
