class Solution {
    public int prefixCount(String[] words, String pref) {
        int len = pref.length();
        int count = 0;
        for(String word : words){
            if(word.length() < len)
                continue;
            if(check(word,pref))
                count++;
        }
        return count;
    }
    private static boolean check(String word, String pref){
        int len = pref.length();
        for(int i = 0; i < len; i++){
            if(word.charAt(i) != pref.charAt(i))
                return false;
        }
        return true;
    }
}
