class Solution {
public:
    int minSteps(string s, string t) 
    {
	// Sort Both the strings.
        sort(s.begin(), s.end());
        sort(t.begin(), t.end());
        int i = 0, j = 0;
        int m = s.size(), n = t.size();
        int count = 0;
        while (i < m && j < n)
        {
		// If character at i and j are not same increment the counter.
            if (s[i] != t[j])
            {
                count++;
            }
			// In case s[i] != t[j] increment the position of the smaller character.
			// If s[i] == t[j] increment the position of both the characters.
            if (s[i] < t[j])
                i++;
            else if (s[i] > t[j])
                j++;
            else
            {
                i++;
                j++;
            }
        }
		// When one of the string is exausted you'll need all the characters from the remaining string to make them anagram of one another.
        if (j < n)
            count += n - j;
        if (i < m)
            count += m - i;
        return count;
    }
};
```

