int minSteps(char * s, char * t){
    int n=strlen(s);
    int m=strlen(t);
    int freq1[26]={0};
    int freq2[26]={0};

    for(int i=0;i<n;i++)
    {
        freq1[s[i]-'a']++;
    }

    for(int i=0;i<m;i++)
    {
        freq2[t[i]-'a']++;
    }

    int s_count=0;
    int t_count=0;

    for(int j=0;j<26;j++)
    {
        if(freq1[j]==freq2[j]);
        else if(freq1[j]>freq2[j])
        {
        t_count=t_count+(freq1[j]-freq2[j]);
        }
        else
        {
        s_count=s_count+(freq2[j]-freq1[j]);
        }
    }
    int result=s_count+t_count;
    return result;
}
