class Solution {
    public long minimumTime(int[] time, int totalTrips) {
        Arrays.sort(time);
        long low = 1, high = Long.MAX_VALUE;
        long minTime = Long.MAX_VALUE;
        while(low<=high){
            long mid = low + (high-low)/2;
            long numTrips = 0;
            for(int t: time){
                // if repeated summation of large mid values(comparable to Long.MAX_VALUE) and small trip time 't' causes overflow - identify, bring upper bound down and break the loop
                numTrips += mid/t;
                if(numTrips<0)
                {
                    high = mid-1;
                    break;
                }
            }
            if(numTrips<0)
                continue;

            if( numTrips >= totalTrips){
                high = mid-1;
                minTime = Math.min(mid, minTime);
            }else if(numTrips < totalTrips){
                low = mid+1;
            }
        }
        return minTime;
    }
}

