    const midTime = Math.floor((minTime + maxTime) / 2);
    let trips = 0;
    for (const tripTime of time) {
      trips += Math.floor(midTime / tripTime);
    }

    trips < totalTrips ? (minTime = midTime + 1) : (maxTime = midTime);
