      int minTime = 0;

      while (minTime <= maxTime) {
          int middle = (maxTime + minTime) ~/ 2;

          //count trips(middle)
          int trips = 0;
          for (int busTime in time) {
              trips += middle ~/ busTime;
          }

          if (trips >= totalTrips) {
              maxTime = middle - 1;
          }
          else {
              minTime = middle + 1;
          }
      }

      return minTime;
  }
}
