/**
 * @param {number[]} time
 * @param {number} totalTrips
 * @return {number}
 */
var minimumTime = function(time, totalTrips) {
    let min = 10**9;
    for(let t of time) {
        min = Math.min(t, min);
    }
    let left = 1;
    let right = min * totalTrips;
    function canComplete(possibleTime) {
        let curTrip = 0;
        for(let busTime of time) {
            curTrip += Math.floor(possibleTime/busTime);
            if(curTrip >= totalTrips) {
                return true;
            }
        }
        return false;
    }
    while(left < right) {
        let midTime = left + Math.floor((right - left)/2);
        let possible = canComplete(midTime);
        if(possible) {
            right = midTime;
        } else {
            left = midTime + 1;
        }
    }
    return right;
};

