class Solution {
public:
    long long minimumTime(vector<int>& time, int totalTrips) {
        long long left = 0;
        long long right = *max_element(time.begin(), time.end()) * (long long)totalTrips;

        while (left < right) {
            long long mid = (left + right) / 2;
            long long trips = 0;

            for (int t : time) {
                trips += mid / t;
            }

            if (trips < totalTrips) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }

        return left;
    }
};

