class Solution {
public:
    bool isPossible(vector<int>&time, long long currentTime, int totalTrips)
    {
        long long maximumTrip = 0;


        for(auto &it : time)
        {
            maximumTrip+= currentTime/it;
        }

        return maximumTrip>=totalTrips;
    }
    long long minimumTime(vector<int>& time, int totalTrips) {

        int n = time.size();


        long long start = 1;

        long long last = (long long) *min_element(begin(time), end(time)) * totalTrips;

        // Apply Binary Search in this range


        while(start<last)
        {
            long long mid = start + (last-start)/2;

            if(isPossible(time,mid,totalTrips))
            {
                // if it is true then no need to search in the part which is grater than mid; so reduce the search space and check for left side if there any minimum number is possible.

                last = mid;
            }
            else
            {
                start = mid+1;
            }
        }


        return start;

    }
};
