class Solution {
    public long minimumTime(int[] time, int totalTrips) {
        //range based binary search
        //the start we will make the minimum value possible
        long s = 1;
        //end we will make the max time possible 
        //which will be the time required by minimum speed bus to complete n no. of trips   
        long min = time[0];
        for(int i = 0;i < time.length;i++){
            min = Math.min(min, time[i]);
        }
        long e = min * totalTrips;
        //so we know that our answer will lye btw the start s and end e
        while(s < e){
            long m = s + (e - s) / 2;
            //now check how many trips can be completed in this m amount of time
            long trips = 0;
            for(int i = 0;i < time.length;i++){
                trips += m / time[i];
            }
            if(trips < totalTrips){
                //we have to move the start pointer
                //since our trips is not enough
                s = m + 1;
            }else{
                //we have to move our end point
                e = m;
            }
        }
        return s;
    }
}
