class Solution {
public:
    bool canComplete(long long t, int totalTrips, vector<int> &time){

        long long trips = 0;
        for(long long i = 0;i<time.size();i++){
            trips += (t/time[i]);
            if(trips >= totalTrips){
                return true;
            }
        }
        return false;
    }

    long long minimumTime(vector<int>& time, int totalTrips) {

        long long sum = 0;
        for(auto v : time){
            sum += v;
        }

        long long lo =  *min_element(time.begin(), time.end());
        long long hi = sum * totalTrips;
        long long mid;

        while(lo < hi){
            mid = lo + (hi-lo)/2;
            if(canComplete(mid,totalTrips,time)){
                hi = mid;
            }
            else{
                lo = mid+1;
            }
        }

        return lo;
    }
};
```

