#define ll long long 

class Solution {
public:

    // Intuition -> we have to fill  the dp array , pre calculations

    // we having dp array of size NumLaps

    // we can take any of the tires any no of times , but  taking changetime

    // we change to a new tyre.

    // Now startingly in say we are at 4th lap , dp[4] will store the minimum 

    // time to cover 4 laps 

    // we will precompute to the do the consecutive k - laps of each types of tyres

    // it will help in finding , if some tyres working very good i.e taking 

    // very less time till k - consecutive laps (k can be 2 to atmost 19)

    // as 2^19 is greater than 10^6 which is greater than max value of change time.

    int minimumFinishTime(vector<vector<int>>& tires, int changetime, int numlaps) {

        vector<vector<ll>> precompute(tires.size()+1 , vector<ll>(20 , 1e9));

        for(int i=0;i<tires.size();i++)
        {
            precompute[i][0]=tires[i][0];

            for(int j=1;j<=19;j++)
            {
                ll val=precompute[i][j-1]*tires[i][1];

                if(val>1e9)
                {
                    break;
                }
                else
                {
                    precompute[i][j]=val;
                }
            }
        }

        for(int i=1;i<=19;i++)
        {
             for(int j=0;j<tires.size();j++)
             {
                 precompute[j][i]+=precompute[j][i-1];

                 // this is  the value of time = precompute[i][j] -> ith tasks having 

                 // consecutive (j+1)th no. of times 
             }
        }

        vector<ll> dp( numlaps+1 , INT_MAX);

        dp[0]=0;

        for(int i=1;i<=numlaps;i++)
        {
            if(i<20)
            {   // check with the i- consecutive laps with the jth tyre.
                for(int j=0;j<tires.size();j++)
                dp[i]=min(dp[i] , precompute[j][i-1]);
                // ( we had stored ith consecutive laps value in precompute[j][i-1])
            }

            // we suppose we are at 8th lap , 

            // Possibilty is we had drive till 7th lap , 1 lap by new one.

            //   drive till 6th lap , changed , 2 lap with new tire

            // drive till 5th lap , 3 lap with new tire 

            // drive till 4th lap , 4 lap with new one 

            // drivw till 3rd lap , 5 lap with new one ( now here things are started

            // to reapaet  so we will be having till lap 4 ).

            for(int j=i-1 ; j>=(i/2) ; j--)
            {
                dp[i]=min(dp[i] , dp[j] + dp[i-j] + changetime);
            }
        }

        return dp[numlaps];

    }
};
```

