class Solution:
    def minimumFinishTime(self, tires: List[List[int]], changeTime: int, numLaps: int) -> int:

        # Only minimal tires (such that there is no other tire having *both* lesser f and r) matter

        tires.sort(key=lambda z: (z[0], -z[1]))
        x = []
        min_r = inf
        for t in tires:
            if t[1] < min_r:
                x.append(t)
                min_r = t[1]

        tires = x


        @cache
        def one_tire_run(tire, nlaps):          
            f, r = tires[tire]
            if nlaps == 1:
                return f
            return f + r*one_tire_run(tire, nlaps-1)


        @cache
        def min_time(ntires, nlaps):
            mt = inf

            if ntires > 1:
                mt = min(mt, min_time(ntires - 1, nlaps))

            for i in range(1, nlaps):
                x = one_tire_run(ntires-1, i) + changeTime
                if mt <= x:
                    break
                mt = min(mt, x + min_time(ntires, nlaps - i))

            mt = min(mt, one_tire_run(ntires-1, nlaps))

            return mt

        return min_time(len(tires), numLaps)
