class Solution {
public:
  int minimumFinishTime(vector<vector<int>>& tires, int changeTime, int numLaps) {
    int minF = -1;
    for (int i = 0; i < tires.size(); ++i) {
      if (minF == -1) {
        minF = tires[i][0];
      } else {
        minF = min(minF, tires[i][0]);
      }
    }

    // If the time taken by the tire is more than ub, don't use it any further
    int ub = minF + changeTime;

    // The minimum time to complete laps without tire change
    vector<int> minTimeNoChange(numLaps + 1, -1);
    // Has to be minF for laps = 1
    minTimeNoChange[1] = minF;
    for (int i = 0; i < tires.size(); ++i) {
      // Find the number of laps after which time for lap is >= ub
      int f = tires[i][0], r= tires[i][1];
      int maxLaps = 1.0 + (double)(log((double)ub / f) / log(r));
      if (maxLaps <= 1) continue;

      // Populate minTimeNoChange for these many laps
      for (int laps = 1; laps <= min(maxLaps, numLaps); ++laps) {
        int timeWithTire = (f * (double)(pow(r, laps) - 1)) / (r - 1);
        if (minTimeNoChange[laps] == -1) {
          minTimeNoChange[laps] = timeWithTire;
        }
        minTimeNoChange[laps] = min(timeWithTire, minTimeNoChange[laps]);
      }
    }

    vector<int> minTime(numLaps + 1, -1);
    for (int laps = 1; laps <= numLaps; ++laps) {
      if (minTimeNoChange[laps] != -1) {
        minTime[laps] = minTimeNoChange[laps];
      }
      for (int l = 1; l < laps; ++l) {
        if (minTimeNoChange[l] != -1) {
          if (minTime[laps] == -1) {
            minTime[laps] = minTimeNoChange[l] + changeTime + minTime[laps - l];
          } else {
            minTime[laps] = min(minTime[laps],
                                minTimeNoChange[l] + changeTime + minTime[laps - l]);
          }
        }
      }
    }
    return minTime[numLaps];
  }
};
```

