class Solution:
    def minimumFinishTime(self, tires: List[List[int]], ct: int, nl: int) -> int:
        cn=[]
        dis=[float("inf")]*(nl+1)
        for x,y in tires:
            cnt=-1
            res=0
            while x*(pow(y,cnt+1))<ct+x:
                cnt+=1
                res+=x*(pow(y,cnt))
                if cnt+1<=nl:
                    dis[cnt+1]=min(dis[cnt+1],res)
            cn.append((x,cnt+1,res))
        ans=float("inf")
        res=[]
        dp=[float("inf")]*(nl+1)
        dp[0]=0
        for i in range(1,nl+1):
            if dis[i]!=float("inf"):
                res.append((i,dis[i]))
                dp[i]=dis[i]
        for i in range(nl+1):
            for vl,cs in res:
                if i+vl<=nl:
                    dp[i+vl]=min(dp[i+vl],dp[i]+ct+cs)
        return dp[-1]
