class Solution {
    public int minimumFinishTime(int[][] tires, int changeTime, int numLaps) {


        int[] best = new int[18];
        Arrays.fill(best, Integer.MAX_VALUE);
        int maxLapsTireCanBeUsed = 0;

        // preprocess
        // using each tire fill/update the best times for 18 laps.
        for (int[] tire : tires){
            int totalTime = tire[0], lapTime = tire[0];
            //using a tire update best as far as the time to use the tire is a good option
            // than changing the tire by taking change time penality
            // we do this only for 18 laps as after 18 laps the changetime penality even for 
            // a max value of 10^5 + F MAX of 10^5 will be smaller than the lap time if
            // the same tire is used
            for (int j = 1; j < 18; j++){
                best[j] = Math.min(best[j], totalTime);
                //update laptime and total time for this lap.
                lapTime *= tire[1];
                totalTime += lapTime;

                maxLapsTireCanBeUsed = Math.max(maxLapsTireCanBeUsed, j);
                // if laptime is more than the time to change time + tire[0] .. break.
                if (lapTime > changeTime + tire[0])
                    break;

            }
        }

        int[] dp = new int[numLaps+1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 1; i <= numLaps; i++){
            for (int j = i - 1; j >= 0 && i - j <= maxLapsTireCanBeUsed; j--){
                // if (i - j > maxLapsTireCanBeUsed){
                //     break;
                // }
                dp[i] = Math.min(dp[i], dp[j] + changeTime + best[i - j]);
            }
        }
        return dp[numLaps] - changeTime;
    }
}
