class Solution:
    def minimumFinishTime(self, tires: List[List[int]], changeTime: int, numLaps: int) -> int:
        tires.sort()
        r = float("inf")

        # Remove useless tires
        i = 0
        while i < len(tires):
            if r <= tires[i][1] or tires[0][0] + changeTime < tires[i][0]:
                tires.pop(i)
            else:
                r = min(r, tires[i][1])
                i += 1

        # pre-compute fastest times for some numbers of laps without changing tire up to 17 laps
        # Only go to 17 because this is the most number of laps you could ever reach without wanting to change a tire
        # 2^17 > 10^5 = upper limit of changeTime, kind of hacky

        one_tire = [float("inf")] * (numLaps + 1)
        one_tire[0] = 0

        for i in range(1, min(numLaps+1, 17)):
            minTime = float("inf")
            for tire in tires:
                time = 0
                for j in range(1, i+1):
                    time += tire[0]*tire[1]**(j - 1)
                minTime = min(minTime, time)
            one_tire[i] = minTime




        @lru_cache(None)
        def dp(laps):
            if laps == 0:
                return 0
            best = one_tire[laps]
            for i in range(1, laps):
                best = min(best, dp(laps - i) + one_tire[i] + changeTime)
            return best
        return dp(numLaps)

