            int curLap = 0;
            ll curLapTime = f;
            int totalTime = 0;

            do {
                totalTime += curLapTime;
                if (curLap < bestOn1Tire.size()) bestOn1Tire[curLap] = min(bestOn1Tire[curLap], totalTime);
