    int f(int n,int changeTime,vector<int>& dp){
        if(n==0) return -changeTime;

        if(dp[n] != -1) return dp[n];

        int mini=1e9;
        for(int laps=1;laps<=min(n,maxlaps);laps++){
            int time = best[laps] + changeTime + f(n-laps,changeTime,dp);
            mini = min(mini,time);
        }
