    int minimumFinishTime(vector<vector<int>>& tires, int changeTime, int numLaps) {
        for(int i=0;i<=numLaps;i++){
            best[i]=INT_MAX;
        }
        vector<int> dp(numLaps+1,-1);

        for(auto it:tires){
            int fi=it[0];
            int ri=it[1];
            long long time=fi;
            long long currlaptime=fi;
            for(int lap=1; lap<=numLaps && currlaptime < fi + changeTime;lap++){


