/**
 * @param {number[][]} tires
 * @param {number} changeTime
 * @param {number} numLaps
 * @return {number}
 */
var minimumFinishTime = function(tires, changeTime, numLaps) {
    // precompute lap times without changing tires
    const lapTimes = Array(numLaps + 1).fill(Infinity)
    for (let [f,r] of tires) {
        let time = 0
        for (let i = 1; i <= numLaps; i++) {
            time += f * (r**(i-1))

			// no need to calculate rest as we're going to change tires
			// changeTime <= 10^5  and for f:1, r:2 total time is 131072 at 18th lap.
			// it's bigger than 10^5 so better change tires
            if (time > 2**17) break 

            lapTimes[i] = Math.min(lapTimes[i], time)
        }
    }

    // calculate changing tires between [1 - i] => 1 --- j --- i
    for (let i = 1; i <= numLaps; i++) {
        if (i >= 18) lapTimes[i] = Infinity // force changing the tire as it's faster after lap 18

        for (let j = 1; j < i; j++) {
            lapTimes[i] = Math.min(lapTimes[i], lapTimes[j] + changeTime + lapTimes[i-j])
        }    
    }

    return lapTimes[numLaps]
};
