if one tire type is better than the other in every way, we can prune the other tire away.
if tow tire types are the same, of course we just need to keep one of them.

```
    struct TireType {
        /// `f`
        int firstLap;
        /// `r`
        int repeatedLapFactor;

        TireType(int _firstLap, int _repeatedLapFactor)
                : firstLap(_firstLap), repeatedLapFactor(_repeatedLapFactor) {}

        /// \brief if this tire type is better than the other in every way, or the two are the same,
        /// return true.
        /// \returns false if each type of tire has its own strength.
        bool dominatesOrEquals(const TireType& other) const {
            return firstLap <= other.firstLap && //
                    repeatedLapFactor <= other.repeatedLapFactor;
        }
    };

    /// Prune the types of tires.
    /// O(n2)
    static vector<TireType> PruneTires(const vector<vector<int>>& tires) {
        if (tires.empty()) {
            return {};
        }
        list<TireType> dominanceTires; // list<T> is excellent for random deletion.
        dominanceTires.emplace_back(tires[0][0], tires[0][1]);

        for (int i = 1; i < tires.size(); ++i) {
            auto newTireType = TireType(tires[i][0], tires[i][1]);
            // if the new tire type dominates an existing tire type `et`,
            // then we'll erase/delete the existing `et`.
            // if the new tire type is dominated by `et`, then we'll skip this new tire type (i.e.
            // we won't add it to the list).
            bool shouldInsert = true;
            for (auto iter = dominanceTires.begin(); iter != dominanceTires.end();) {
                // if the new tire type is dominated by `et`, then we'll skip this new tire type
                // (i.e. we won't add it to the list).
                if (iter->dominatesOrEquals(newTireType)) {
                    shouldInsert = false;
                    break;
                }
                if (newTireType.dominatesOrEquals(*iter)) {
                    // new tire dominates the existing one ==> delete the existing one.
                    iter = dominanceTires.erase(iter);
                } else { // they don't dominate each other -- they have their own strength.
                    ++iter;
                }
            }
            if (shouldInsert) {
                dominanceTires.emplace_back(newTireType);
            }
        }

        cout << "Pruned " << tires.size() - dominanceTires.size() << " tires out of "
             << tires.size() << ", or "
             << 100.0 * double(tires.size() - dominanceTires.size()) / tires.size() << "%." << endl;

        // sort the tires by firstLap
        vector<TireType> goodTires(dominanceTires.begin(), dominanceTires.end());
        sort(goodTires.begin(), goodTires.end(), [](const TireType& a, const TireType& b) {
            return a.firstLap < b.firstLap
                    || a.firstLap == b.firstLap && a.repeatedLapFactor < b.repeatedLapFactor;
        });
        return goodTires;
    }
