class Solution {
    public int minimumFinishTime(int[][] tires, int changeTime, int numLaps) {
        return helper(tires, changeTime, numLaps, 1, Integer.MIN_VALUE, 0);
    }
    int time = 0;
    int maxtime = 0;
    private int helper(int[][] tires, int changeTime, int numLaps, int start, int tireno, int tirelaps){
        int time1= Integer.MAX_VALUE;
        int time2= Integer.MAX_VALUE;
        if(start == numLaps+1){
            return 0;
        }

        for(int i = 0; i < tires.length; i++){
            if(i == tireno){
                time2 = Math.min(
                    (timeCalc(tires[i], tirelaps) + helper(tires, changeTime, numLaps, start+1, i, tirelaps+1)),
                    ((timeCalc(tires[i], 1) + changeTime) + helper(tires, changeTime, numLaps, start+1, i, 2))
                );
            }else{
                time = timeCalc(tires[i], 1) + changeTime;
                time1 = (time + helper(tires, changeTime, numLaps, start+1, i, 2));
            }
            maxtime = Math.min(time1,time2);
        }
        return maxtime;
    }

    private int timeCalc(int[] tire, int x){
        int time = tire[0]*(int)Math.pow(tire[1], x-1);
        return time;
    }
}
