    int minimumFinishTime(vector<vector<int>>& tires, int changeTime, int numLaps) {
        vector<vector<long long> >precompute(tires.size(),vector<long long>(19,200000000));
        for(int i=0;i<tires.size();i++){


            precompute[i][0]=tires[i][0];

            for(int j=1;j<19;j++){
                long long check=precompute[i][j-1]*tires[i][1];
                if(check>=200000000){
                    break;
                }
                else{
                    precompute[i][j]=check;
                }
            }



            for(int j=1;j<19;j++){
                precompute[i][j]+=precompute[i][j-1];

                if(precompute[i][j]>=200000000){
                    precompute[i][j]=200000000;
                    break;
                }
            }

        }




        vector<long long>dp(numLaps+1,INT_MAX);



        for(int i=1;i<=numLaps;i++){
            if(i<20){

                for(int j=0;j<tires.size();j++){
                    dp[i]=min(dp[i],precompute[j][i-1]);
                }

            }
             for(int j=i/2;j<i;j++){
                    dp[i]=min(dp[i],dp[i-j]+dp[j]+changeTime);
                }
        }
        return dp[numLaps];
    }
};
