**Top Down approach**

```
class Solution {
private:
    int f(int n, int changeTime, vector <int> &dp, vector <int> &best, int maxLaps){

        if(n == 0) return -changeTime;

        int mini = 1e9;

        if(dp[n] != -1) return dp[n];

        // taking the best laptime for every lap and checking all the combinations and storing the minimum value

        for(int laps = 1; laps <= min(maxLaps, n); laps++){
            int time = best[laps] + changeTime + f(n - laps, changeTime, dp, best, maxLaps);
            mini = min(mini, time);
        }

        return dp[n] = mini;
    }
public:
    int minimumFinishTime(vector<vector<int>>& tires, int changeTime, int numLaps) {
        vector<int> best(numLaps + 1, 1e9);
        int maxLaps = 0;

        //calculate the best lap time for every lap
        for(auto tire : tires){
            int f = tire[0];
            int r = tire[1];


            long long  currLapTime = f;
            long long time = f;

            //getting the lap time for the current tire
            for(int laps = 1; laps <= numLaps and currLapTime < changeTime + f; laps++ ){

                // if the current time is less than the best lap time for the current lap
                if(time < best[laps]){
                    best[laps] = time;
                }

                // storing the maxLaps
                maxLaps = max(maxLaps, laps);

                //incrementing the currentLapTime and time
                currLapTime *= r;
                time += currLapTime;

            }
        }

         vector <int> dp(numLaps + 1, -1);

        return f(numLaps, changeTime, dp, best, maxLaps);
    }
};
```

