class Solution {
    vector<int> bestTime;
    vector<int> dp;
    int getMinTime(int numLaps, int changeTime) {
        if(dp[numLaps - 1] != -1) return dp[numLaps - 1];
        int minTime = 1e9;
        if(bestTime[numLaps - 1] != 1e9) minTime = bestTime[numLaps - 1];
        // MCM variation
        for(int k = 1; k <= numLaps / 2; k++) {
            minTime = min(minTime, getMinTime(k, changeTime) + getMinTime(numLaps - k, changeTime) + changeTime);
        }
        return dp[numLaps - 1] = minTime;
