type Tire = [number, number];

function minimumFinishTime(tires: Tire[], changeTime: number, numLaps: number) {
  // For a specific tire, calculate how long a specific lap takes. For example, the tire
  // [3,2] takes 12 seconds to complete the third lap.
  const secondsForSpecificLap = (tire: Tire, lap: number) => tire[0] * tire[1] ** (lap - 1);

  // Figure out how long you can use a specific tire before it should be changed. At a certain
  // point, the tire is slow enough that it makes sense to pay the changeTime penalty to get a
  // fast new tire. This decreases overall running time because it avoids calculations that
  // involve very old tires.
  const maxLapsForTire = (tire: Tire) => {
    const getNewTire = secondsForSpecificLap(tire, 1) + changeTime;
    for (let laps = 2; laps <= 1000; laps++) {
      if (secondsForSpecificLap(tire, laps) > getNewTire) return laps - 1;
    }
    return 1000;
  };

  // Figure out how long it takes to complete a certain number of laps with a particular
  // tire, assuming the tire is never changed. The result does not include the initial time
  // required to install the tire.
  const secondsForNLaps = (tire: Tire, laps: number) => {
    let lap = 1;
    let result = 0;
    while (lap <= laps) {
      result += secondsForSpecificLap(tire, lap);
      lap++;
    }
    return result;
  };

  // Calculate the fastest way to travel a certain number of laps, assuming no tire changes.
  // This simply walks through every tire and every number of possible laps that tire can
  // complete (before it should be changed), and tracks the fastest/minimum seconds for that
  // number of laps. For example, if tire A completes 3 laps in 5 seconds, tire B completes
  // 3 laps in 2 seconds, and tire C completes 3 laps in 8 seconds, we'll store that 3 laps can
  // be completed in 2 seconds, since that is the minimum of 5, 2, and 8.
  const minSecondsForNLaps: Map<number, number> = new Map();
  for (const t of tires) {
    for (let laps = 1; laps <= maxLapsForTire(t); laps++) {
      const duration = secondsForNLaps(t, laps);
      const fastest = minSecondsForNLaps.get(laps);
      if (fastest === undefined || duration < fastest) {
        minSecondsForNLaps.set(laps, duration);
      }
    }
  }

  // Just do a DFS to figure out the fastest completion time. At each step, consider every
  // possible number of laps that can be completed before a tire change.
  const memo: Map<number, number> = new Map();
  const minFinishTime = (numLaps: number): number => {
    if (numLaps === 0) return 0;
    let fastest = memo.get(numLaps);
    if (fastest !== undefined) return fastest;
    fastest = Number.POSITIVE_INFINITY;
    for (let laps = 1; laps <= Math.min(minSecondsForNLaps.size, numLaps); laps++) {
      const seconds = changeTime + minSecondsForNLaps.get(laps)! + minFinishTime(numLaps - laps);
      fastest = Math.min(fastest, seconds);
    }
    memo.set(numLaps, fastest);
    return fastest;
  };
  return minFinishTime(numLaps) - changeTime;
}
