class Solution {
    using integer = long long;

public:
    int minimumFinishTime(vector<vector<int>>& tires, int changeTime, int numLaps) {
        const integer change_time = changeTime;
        const integer num_laps = numLaps;    

        integer min_time = INT_MAX;
        for (int i = 0; i < tires.size(); ++i) {
            min_time = std::min(change_time + tires[i][0], min_time);
        }
        const integer finish_time_upper_bound = min_time * num_laps - change_time;

        std::vector<integer> lap_to_min_cost(num_laps + 1, finish_time_upper_bound);
        int max_conti_lap = 0;
        for (int i = 0; i < tires.size(); ++i) {
            const integer f = tires[i][0], r = tires[i][1];
            integer pow = 1;
            integer sum = pow;
            for (int lap = 1; lap <= num_laps; ++lap) {
                const integer cost = f * sum;
                if (cost > finish_time_upper_bound) {
                    break;
                }
                max_conti_lap = std::max(lap, max_conti_lap);
                lap_to_min_cost[lap] = std::min(
                    cost, lap_to_min_cost[lap]
                );
                pow *= r;
                sum += pow;
            }
        }

        std::vector<integer> tbl(num_laps + 1, finish_time_upper_bound);
        tbl[0] = 0;
        for (int lap = 1; lap <= num_laps; ++lap) {
            integer min_cost = min_time * lap - change_time;
            if (lap <= max_conti_lap) {
                min_cost = std::min(lap_to_min_cost[lap], min_cost);
            }
            for (int suff_len = 1; suff_len <= std::min(lap, max_conti_lap); ++suff_len) {
                const int pref_len = lap - suff_len;
                min_cost = std::min(
                    tbl[pref_len] + change_time + lap_to_min_cost[suff_len],
                    min_cost
                );
            }
            tbl[lap] = min_cost;
        }
        return tbl.back();
    }
};
```

