class Solution {
    public int mostFrequent(int[] nums, int key) {
        HashMap<Integer, Integer> map = new HashMap<>();

        // Iterate through nums for n - 1 times
        for(int i = 0; i < nums.length - 1; i++){
            if(nums[i] == key){
                // Put the next element into the map and increment its value
                map.put(nums[i+1], map.getOrDefault(nums[i+1], 0) + 1);
            }
        }

        int output = 0; // To store the target
        int maxCount = 0; // To set the target with maximum frequency

        // Loop through the map
        for (var entry : map.entrySet()) {
            if(maxCount < entry.getValue()){ // If the value of current entry is greater than maxCount
                maxCount = entry.getValue(); // Set the maxCount to that value
                output = entry.getKey(); // Set that value's key to output
            }
        }

        return output;
    }
}
