public int mostFrequent(int[] nums, int key) {
	var targetCount = new HashMap<Integer, Integer>();

	IntStream.iterate(0, i -> i + 1 < nums.length, i -> i + 1)
			 .filter(i -> nums[i] == key)
			 .forEach(i -> targetCount.compute(nums[i + 1], (k, v) -> v == null ? 1 : ++v));

	return Collections.max(targetCount.entrySet(), Comparator.comparingInt(Map.Entry::getValue))
					  .getKey();
}
