        int mx = INT_MIN;
        int target = -1;                                 // initialize target with -1   

        for(auto it : mp)      // iterate upon the map
        {
            if(mx<it.second) // if found any max frequent element just store that 
            {
                mx = it.second;           // storing that element frequency count
                target = it.first;        // storing that element
            }
        }
        return target;
    }
