class Solution {
    public int mostFrequent(int[] nums, int key) {

        int n = nums.length;
        int[] targetFreqs = new int[1001];

        for(int i = 0; i < n - 1; i++){

            if(nums[i] == key){
                int target = nums[i + 1];
                targetFreqs[target]++;
            }
        }

        int target = -1;
        int maxOccur = -1;
        for(int val = 1; val <= 1000; val++){
            if(targetFreqs[val] > maxOccur) {
                maxOccur = targetFreqs[val];
                target = val;
            }
        }
        return target;
    }
}
