class Solution {
public:
    int mostFrequent(vector<int>& nums, int key) {
        std::unordered_map<int, int> m;
        for (int i = 0; i < nums.size() - 1; i++)
        {
            if (nums[i] == key)
            {
                m[nums[i+1]]++;
            }
        }
        std::pair<int, int> res = *(m.begin());
        for (auto p: m)
        {
            if (p.second > res.second)
            {
                res = p;
            }
        }
        return res.first;
    }
};
