        nums_dict = dict.fromkeys(nums, 0)
        for target in nums_dict:
            for i in range(0, len(nums) - 1):
                if (nums[i] == key and nums[i + 1] == target):
                    nums_dict[target] += 1
        return max(nums_dict, key = nums_dict.get)
