class Solution {
    func mostFrequent(_ nums: [Int], _ key: Int) -> Int {
        var frequencies = [Int]()
        var keyNumbers = [Int]()
        var lastKey = false

        for num in nums{
            if(lastKey){
                if(keyNumbers.contains(num)){
                    if let index = keyNumbers.index(of: num){
                        frequencies[index] += 1
                    }
                } else{
                    keyNumbers.append(num)
                    frequencies.append(1)
                }
            }

            if(num == key){
                lastKey = true
            } else{
                lastKey = false
            }
        }

        var highestFreq = 0
        var output: Int = 0;
        for (i, num) in keyNumbers.enumerated(){
            var freq = frequencies[i]
            if(freq > highestFreq){
                output = num
                highestFreq = freq
            }
        }
        return output
    }
}
```

