    int mostFrequent(vector<int>& nums, int key) {
        unordered_map<int,int> table;
        for(int i=0;i<nums.size()-1;i++){
            if(nums[i]==key)
                table[nums[i+1]]++;
        }
        int max=0;
        int maxIndex=0;
        for(auto m:table){
            if(m.second > max){
                max = m.second;
                maxIndex=m.first;
            }
        }
        return maxIndex;
    }
};
