```
/*

    Time Complexity : O((2^2*N)*N). For each of 2^2*N sequences, we need to create and validate the sequence,
    which takes O(N) work.

    Space Complexity : O((2^2*N)*N) vector(output) space.

    Solved using Array + Backtracking. Brute Force Approach.

*/


/***************************************** Approach 1 *****************************************/

class Solution {
private:
    bool valid(vector<char> temp){
        int balance = 0;
        for(auto c : temp){
            if(c == '(') balance++;
            else balance--;
            if(balance < 0) return false;
        }
        return balance == 0;
    }
    void generateAllParenthesis(vector<string>& parenthesesCombinations, vector<char> temp, int position){
        if(position == temp.size()){
            if(valid(temp)){
                string s(temp.begin(), temp.end());
                parenthesesCombinations.push_back(s);
            }
            return;
        }
        temp[position] = '(';
        generateAllParenthesis(parenthesesCombinations, temp, position+1);
        temp[position] = ')';
        generateAllParenthesis(parenthesesCombinations, temp, position+1); 
    }
public:
    vector<string> generateParenthesis(int n) {
        vector<string> parenthesesCombinations;
        vector<char> temp(2*n);
        generateAllParenthesis(parenthesesCombinations, temp, 0);
        return parenthesesCombinations;
    }
};






/*

    Time Complexity : O(O(2^2*N)The time complexity of the above code is O(2^(2N)) since in the worst case we
    need to consider every possibility of opening and closing brackets where N = the number of pairs we need to
    form.

    Space Complexity : O((2^2*N)*N) vector(output) space.

    Solved using Array + Backtracking. Brute Force Approach.

*/

/***************************************** Approach 2 *****************************************/

class Solution {
private:
    void recurse(vector<string>& output, string s, int open, int close, int n){
        if(open==n and close==n){
            output.push_back(s);
            return;
        }
        if(open<n)
            recurse(output, s+"(" , open+1, close, n);
        if(close<open)
            recurse(output, s+")", open, close+1, n);
    }
public:
    vector<string> generateParenthesis(int n) {
        vector<string> output;
        recurse(output, "", 0, 0, n);
        return output;
    }
};

