class Solution {
public:
   static bool isValid(string m)
    {
        if(m[0]==')')
            return false;
        stack<string> s;
        for(int i =0;i<m.length();i++)
        {
            if(m[i]=='(')
                s.push("(");
            else if(s.empty()&&m[i]==')')
                    return false;
            else if(m[i]==')')
                s.pop();
        }
        if(s.empty())
            return true;
        else
            return false;
    }
    vector<string> generateParenthesis(int n) {
        string s;
        vector<string> v;
        for(int i =0;i<n;i++)
        {
            s+="(";
        }
        for(int i =0;i<n;i++)
        {
            s+=")";
        }
        v.push_back(s);
        while(next_permutation(s.begin(),s.end()))
        {
            if(isValid(s))
                v.push_back(s);
        }      
        return v;
    }
};
