     /**
     * @param curStr       The result of the current recursion
     * @param left          How much left bracket has been used
     * @param right  
     * @param n      
     * @param res         result set
     */

    public void dfs(String curstr, int left, int right, int n, List<String> res){
        if(left ==n && right ==n){
            res.add(curstr);
            return;
        }
        //unsatisfied condition, cut off
        if(left < right){
            return;
        }

        if(left<n){
            dfs(curstr+"(",left+1,right,n,res);
        }

         if(right<n){
            dfs(curstr+")",left,right+1,n,res);
        }

    }
}
